       doc.addImage(imgData, 'JPEG', 20, 15, 76, 16); // left, top, width, height (remember if mm or inches or whatever)
     
        var imgData = 'data:image/jpeg;base64,'
       doc.addImage(imgData, 'JPEG', 135, 15, 56, 16); // left, top, width, height (remember if mm or inches or whatever)

